import courses from '../data/courses.json'
import './Courses.css';
import {useState} from 'react';


function TheCourse({depts, numbers, title, description}: any ){

  const [isShown, setIsShown] = useState(false);

  function handleClick(){
    setIsShown(!isShown);
  };



  return <div>
    <div className = 'courseHead' onClick = {handleClick} >
    <div className = 'courseID'>
      {depts} {' '} {numbers}
    </div>
    <div className = 'title'> {title} </div>
    </div>
      
      {isShown && <div onClick = {handleClick}> {description} </div>}

      <button className = "addBut">Add to cart</button>






  </div>;
}

function Courses({theSearch, filts} : any) {

  const filtCourse = courses.filter(function(entry){


    return (theSearch === "" || theSearch === " "
    || entry.dept.includes(theSearch) ||
    entry.description.includes(theSearch) ||
    entry.title.includes(theSearch) ||
    entry.number.toString().includes(theSearch)) &&
    ((filts[0]&& entry.number >= 100 && entry.number < 200 )
    || (filts[1] && entry.number >= 200 && entry.number <300)
    || (filts[2] && entry.number >= 300 && entry.number < 400) 
    || (filts[3] && entry.number >= 400 && entry.number < 500)
    );

  })

  return (<>
    {filtCourse.map(({ dept, number, title, prereqs, description }) => (

      <TheCourse key={`${dept}-${number}`} depts={dept} numbers={number} title = {title} description = {description}/>
      
    ))}
  </>);
}

export default Courses;
