import './App.css';

import Nav from './components/Nav';
import Courses from './components/Courses';
import Cart from './components/Cart';
import Filter from './components/Filter';

import {useState} from 'react';


function App() {

  const [search, setSearch] = useState(" ");
  const [filt, setFilt] = useState([true, true, true, true]);
  const[cart, setCart] = useState([{'number': 0}])


  function handleSearch(inp: any){
    setSearch(inp);
  };

  function addCart(inp: any){
    setCart([inp, ...cart]);
  }

  function delCart(inp: any){
    console.log(inp)
    setCart(cart.filter(function(entry){
      return entry.number != inp


    }))
  }

  return (
    <>
      <Nav setSearch = {handleSearch}/>
      <div className = "content">
        <div className = "right">
        <Cart acs = {cart} dcs = {delCart} />
        <Filter f = {filt} s = {setFilt}  />
        </div>
        <div className = "courses">
        <Courses  theSearch = {search} filts = {filt} scs = {addCart} cart = {cart} dcs = {delCart}/>
        </div>
      </div>
    </>
  );
}

export default App;
